import React, { useState } from "react";
import { postUsers } from "../../services/user";
import { AppContainer, Header, FormContainer } from "../../components/CadastroForm/index";
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { IMaskInput } from "react-imask";
import { useNavigate  } from "react-router-dom";


function Cadastro() {

    const navigate = useNavigate();

    const [showPassword, setShowPassword] = useState(false);

    // Declaração dos estados utilizando o hook useState
    const [message, setMessage] = useState("");

    const formik = useFormik({
        initialValues: {
          name: '',
          cpf: '',
          birthday: '',
          phone: '',
          email: '',
          password: '',
          role: '',
          address: {
            cep: '',
            street: '',
            city: '',
            state: ''
          }
        },
        validationSchema: Yup.object({
          // Campos de validação existentes...
          address: Yup.object().shape({
            cep: Yup.string().required('Obrigatório'),
            street: Yup.string().required('Obrigatório'),
            city: Yup.string().required('Obrigatório'),
            state: Yup.string().required('Obrigatório')
          })
        }),
      
        onSubmit: (values) => {
          // Função para lidar com o envio do formulário
          const userData = {
            name: values.name,
            email: values.email,
            phone: values.phone,
            password: values.password,
            birthday: values.birthday,
            role: values.role,
            address: {
              cep: values.address.cep,
              street: values.address.street,
              city: values.address.city,
              state: values.address.state
            }
          };
          console.log(userData);
        }
      });
      

    //"Ocultar senha" : "Mostrar senha"
    const togglePasswordVisibility = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return ( 
        
        <div className="relative ml-[195px] -mt-[650px] ">
            <AppContainer className="">
                <FormContainer className="mt-16 flex ml-12">
                    <div className="flex text-#0588D1 ">
                        <form onSubmit={formik.handleSubmit} className="">
                            <div className="grid grid-flow-col mb-6 gap-5">
                                {/* Campos de formulário existentes... */}
                                
                                {/* Novos campos de endereço */}
                                <div className="">
                                    <label htmlFor="address.cep" className="block text-sm font-medium">
                                        CEP
                                    </label>
                                    <input
                                        id="address.cep"
                                        name="address.cep"
                                        type="text"
                                        placeholder="Digite o CEP"
                                        {...formik.getFieldProps('address.cep')}
                                        className="w-42 px-3 py-2 border border-gray-300 rounded-xl focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.address?.cep && formik.errors?.address?.cep ? (
                                        <div className="text-sm">{formik.errors.address.cep}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="address.street" className="block text-sm font-medium">
                                        Rua
                                    </label>
                                    <input
                                        id="address.street"
                                        name="address.street"
                                        type="text"
                                        placeholder="Digite a Rua"
                                        {...formik.getFieldProps('address.street')}
                                        className="w-96 px-3 py-2 border border-gray-300 rounded-xl focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.address?.street && formik.errors?.address?.street ? (
                                        <div className="text-sm">{formik.errors.address.street}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="address.city" className="block text-sm font-medium">
                                        Cidade
                                    </label>
                                    <input
                                        id="address.city"
                                        name="address.city"
                                        type="text"
                                        placeholder="Digite a Cidade"
                                        {...formik.getFieldProps('address.city')}
                                        className="w-96 px-3 py-2 border border-gray-300 rounded-xl focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.address?.city && formik.errors?.address?.city ? (
                                        <div className="text-sm">{formik.errors.address.city}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="address.state" className="block text-sm font-medium">
                                        Estado
                                    </label>
                                    <input
                                        id="address.state"
                                        name="address.state"
                                        type="text"
                                        placeholder="Digite o Estado"
                                        {...formik.getFieldProps('address.state')}
                                        className="w-96 px-3 py-2 border border-gray-300 rounded-xl focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.address?.state && formik.errors?.address?.state ? (
                                        <div className="text-sm">{formik.errors.address.state}</div>
                                    ) : null}
                                </div>
                            </div>

                            <div className="grid grid-flow-col mb-6 gap-5l place-items-end">
                                <button 
                                    type="submit" 
                                    className="w-36 px-3 py-2 bg-#0588D1 mt-8 p-2 text-white rounded-xl hover:bg-#border-#0588D1"
                                >
                                    Cadastrar
                                </button>
                            </div>

                            {/* Exibe a mensagem de sucesso ou erro */}
                            {message && (
                                <p>
                                    <svg className="w-6 h-6 text-red-500 dark:red-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 21">
                                        <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 3.464V1.1m0 2.365a5.338 5.338 0 0 1 5.133 5.368v1.8c0 2.386 1.867 2.982 1.867 4.175C17 15.4 17 16 16.462 16H3.538C3 16 3 15.4 3 14.807c0-1.193 1.867-1.789 1.867-4.175v-1.8A5.338 5.338 0 0 1 10 3.464ZM4 3 3 2M2 7H1m15-4 1-1m1 5h1M6.54 16a3.48 3.48 0 0 0 6.92 0H6.54Z"/>
                                    </svg>
                                    {message}
                                </p>
                            )}
                        </form>
                    </div>
                </FormContainer>
            </AppContainer>
        </div>
    );
}

export default Cadastro;
