import React, { useState } from 'react';

const PetModal = ({ isOpen, formData, onChange, onCancel, onSave }) => {
    const [editedFormData, setEditedFormData] = useState(formData);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setEditedFormData({ ...editedFormData, [name]: value });
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        onSave(editedFormData);
    };

    return (
        isOpen && (
            <div>
                <h2>Editar Pet</h2>
                <form onSubmit={handleSubmit}>
                    <div>
                        <label htmlFor="name">Nome do Pet</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={editedFormData.name}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div>
                        <label htmlFor="age">Idade</label>
                        <input
                            type="text"
                            id="age"
                            name="age"
                            value={editedFormData.age}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div>
                        <label htmlFor="breed">Ra√ßa</label>
                        <input
                            type="text"
                            id="breed"
                            name="breed"
                            value={editedFormData.breed}
                            onChange={handleInputChange}
                        />
                    </div>
                    <button type="submit">Salvar</button>
                    <button onClick={onCancel}>Cancelar</button>
                </form>
            </div>
        )
    );
};

export default PetModal;
