import React, { useState, useEffect } from 'react';
import { patchPet } from '../../services/pets';

function EditPetModal({ petId, petData, onUpdatePet, onClose }) {
    const [editedPetData, setEditedPetData] = useState(petData);

    useEffect(() => {
        // Atualize o estado local de editedPetData com os dados do petData recebido
        setEditedPetData(petData);
    }, [petData]);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setEditedPetData({ ...editedPetData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Faça uma solicitação PATCH para atualizar o pet
            const updatedPet = await patchPet(petId, editedPetData);
            // Chame a função onUpdatePet para atualizar a lista de pets no componente pai
            onUpdatePet(updatedPet);
            onClose(); // Feche o modal de edição
        } catch (error) {
            console.error('Erro ao atualizar o pet:', error);
        }
    };

    return (
        <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-gray-800 bg-opacity-70 z-50">
            <div className="bg-white p-4 rounded-lg shadow-lg">
                <h2 className="text-lg font-semibold mb-4">Editar Pet</h2>
                <form onSubmit={handleSubmit}>
                    <div className="mb-4">
                        <label htmlFor="name" className="block text-sm font-medium">
                            Nome do Pet
                        </label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={editedPetData.name}
                            onChange={handleInputChange}
                            className="w-full border-gray-300 rounded-md shadow-sm"
                        />
                    </div>
                    {/* Adicione campos de entrada para outras informações do pet (idade, raça, etc.) */}
                    <div className="flex justify-end">
                        <button
                            type="submit"
                            className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300"
                        >
                            Salvar
                        </button>
                        <button
                            type="button"
                            onClick={onClose}
                            className="ml-2 bg-gray-300 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-400 focus:outline-none focus:ring focus:ring-gray-300"
                        >
                            Cancelar
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default EditPetModal;
