import React, { useState, useEffect } from 'react';
import SeachAnimal from '../SeachAnimal/index.';
import pata from '../../img/patas.png';
import { getPet, getPets, patchPet, deletePet } from '../../services/pets'; // Importe as funções patchPet e deletePet
import { getUser } from '../../services/user';
import EditPetModal from '../EditPetModal';

function EditarAnimal() {
    const [pets, setPets] = useState([]);
    const [selectedPetId, setSelectedPetId] = useState(null);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [selectedPetData, setSelectedPetData] = useState(null);
    const [dataUpdated, setDataUpdated] = useState(false);
    const [isConfirmModalOpen, setConfirmModalOpen] = useState(false); // Adicione o estado para o modal de confirmação
    const [petToDelete, setPetToDelete] = useState(null); // Adicione o estado para o pet a ser excluído

    useEffect(() => {
        async function fetchPets() {
            try {
                const petList = await getPets();
                const petsWithOwnerNames = await Promise.all(
                    petList.map(async (pet) => {
                        try {
                            const user = await getUser(pet.ownerId);
                            console.log(`Nome do responsável para o pet com ID ${pet.id}: ${user.dataUser.name}`);
                            return { ...pet, ownerName: user.dataUser.name };
                        } catch (error) {
                            console.error(`Erro ao obter o usuário para o pet com ID ${pet.id}:`, error);
                            return { ...pet, ownerName: 'Não encontrado' };
                        }
                    })
                );
                setPets(petsWithOwnerNames);
            } catch (error) {
                console.error('Erro ao obter os pets:', error);
            }
        }

        fetchPets();
    }, [dataUpdated]);

    const handleEditClick = (petId) => {
        setSelectedPetId(petId);
        setEditModalOpen(true);
    };

    const handleEditConfirmation = (petId) => {
        setSelectedPetId(petId);
        setConfirmModalOpen(false);
        setEditModalOpen(true);
    };

    const handleDeleteConfirmation = async (petId) => {
        try {
            await deletePet(petId);
            // Atualize o estado para desencadear a atualização dos dados
            setDataUpdated(!dataUpdated);
        } catch (error) {
            console.error('Erro ao excluir o pet:', error);
        }
        setConfirmModalOpen(false);
    };

    return (
        <div className='relative ml-[195px] -mt-[650px]'>
            <section className="flex justify-between mx-8 mt-4 items-center">
                <SeachAnimal />

                <div className="flex items-center">
                    <a href="/cadastroAnimal" className="w-50 px-3 py-2 bg-#0588D1 p-2 text-white rounded-xl hover:bg-blue-800 flex">
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            fill="none"
                            viewBox="0 0 24 24"
                            strokeWidth={1.5}
                            stroke="currentColor"
                            className="w-6 h-6"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                d="M12 4.5v15m7.5-7.5h-15"
                            />
                        </svg>
                        Cadastrar
                    </a>
                </div>
            </section>

            <table className="mx-8 mt-4 bg-gray-200 rounded-xl mb-10 w-[1100px]">
                <thead className="text-#0588D1">
                <tr className="">
                    <th className="py-2 w-1/4">Nome do responsável:</th>
                    <th className="py-2 w-1/4">Nome</th>
                    <th className="py-2 w-1/4">Idade</th>
                    <th className="py-2 w-1/4">Raça</th>
                    <th className="py-2 w-1/4">Ação</th>
                </tr>
                </thead>
                <tbody>
                {pets.map((pet) => (
                    <tr key={pet.id} className="space-x-8 border-t-2 border-white text-gray-700 text-center">
                        <td className="flex items-center my-2 ml-4 text-center">
                            <span className='flex justify-center w-8 h-8'><img className="h-8 ml-0" src={pata} alt="Imagem" /></span>
                            <span className="ml-2 mr-4">{pet.ownerName}</span>
                        </td>
                        <td className="px-8"><span className='flexe text-black h-8 w-8 rounded-md justify-center'>{pet.name}</span></td>
                        <td className="px-8 ">{pet.age}</td>
                        <td className="px-8 h-8 w-8">{pet.breed}</td>
                        <td className="px-8 ">
                            <button onClick={() => setConfirmModalOpen(true)} className="hover:bg-gray-400 rounded">
                                Editar
                            </button>
                            <button onClick={() => setPetToDelete(pet)} className="hover:bg-gray-400 rounded ml-2">
                                Excluir
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>

            {isEditModalOpen && (
                <EditPetModal
                    petId={selectedPetId}
                    petData={selectedPetData}
                    onClose={() => setEditModalOpen(false)}
                    onUpdatePetData={() => setDataUpdated(!dataUpdated)} // Atualize dataUpdated após a edição
                />
            )}

            {/* Modal de confirmação */}
            {isConfirmModalOpen && (
                <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-gray-800 bg-opacity-70 z-50">
                    <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                        <h2 className="text-2xl font-semibold mb-4">Confirmação</h2>
                        <p>Deseja editar ou excluir este pet?</p>
                        <div className="flex justify-end mt-4">
                            <button
                                onClick={() => handleEditConfirmation(selectedPetId)}
                                className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300"
                            >
                                Editar
                            </button>
                            <button
                                onClick={() => handleDeleteConfirmation(petToDelete.id)}
                                className="ml-2 bg-red-500 text-white py-2 px-4 rounded-md hover:bg-red-600 focus:outline-none focus:ring focus:ring-red-300"
                            >
                                Excluir
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default EditarAnimal;
