import React, { useState } from "react";
import { postUsers } from "../../services/user";
import { AppContainer, Header, FormContainer } from "../../components/CadastroForm/index";
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { IMaskInput } from "react-imask";
import { useNavigate  } from "react-router-dom";
import ShowPasswordImg from "../../img/ShowPasswordImg 1.png";
import HidePasswordImg from "../../img/HidePasswordImg 1.png";

function CadastroAnimal() {
    const navigate = useNavigate();

    const [showPassword, setShowPassword] = useState(false);

    const [message, setMessage] = useState("");

    const formik = useFormik({
        initialValues: {
            name: '', // Nome do animal
            age: '', // Idade do animal
            type: '', // Tipo do animal (ex: dog, cat)
            breed: '', // Raça do animal
            ownerId: '', // ID do responsável (dono) do animal
        },
        validationSchema: Yup.object({
            name: Yup.string().required('Obrigatório'),
            age: Yup.number().required('Obrigatório'),
            type: Yup.string().required('Obrigatório'),
            breed: Yup.string().required('Obrigatório'),
            ownerId: Yup.number().required('Obrigatório'),
        }),

        onSubmit: values => {
            const animalData = {
                name: values.name,
                age: values.age,
                type: values.type,
                breed: values.breed,
                ownerId: values.ownerId,
            };

            console.log(animalData);

            // Chamada da função para cadastrar o animal
            // Substitua 'postUsers' pela função apropriada para cadastrar o animal
            // Certifique-se de que a função espera os dados corretos para o cadastro de animais
            // Ela pode se chamar 'postAnimals', por exemplo.
            // postAnimals(animalData)
            //     .then((response) => {
            //         if (response.success) {
            //             setMessage(response.message);
            //         } else {
            //             setMessage(response.message);
            //         }
            //     })
            //     .catch((error) => {
            //         console.error("Erro ao cadastrar animal:", error);
            //         setMessage("Erro ao cadastrar animal. Por favor, tente novamente.");
            //     });

            // Após o cadastro, você pode redirecionar o usuário para a página desejada, se necessário.
            // navigate('/outrapagina');
        },
    })

    const togglePasswordVisibility = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return ( 
        <div className="relative ml-[195px] -mt-[650px]">
            <AppContainer className="">
                <FormContainer className="mt-16 flex ml-12">
                    <div className="flex text-#0588D1">
                        <form onSubmit={formik.handleSubmit} className="">
                            <div className="grid grid-flow-col mb-6 gap-5">
                                <div className="">
                                    <label htmlFor="name" className="block text-sm font-medium">
                                        Nome do animal
                                    </label>
                                    <input
                                        id="name"
                                        name="name"
                                        type="text"
                                        placeholder="Digite o nome do animal"
                                        {...formik.getFieldProps('name')}
                                        className="w-[30rem] focus:border-#0588D1 focus:border-[2.8px] focus:outline-none px-3 py-2 border border-gray-300 rounded-xl"
                                    />
                                    {formik.touched.name && formik.errors.name ? (
                                        <div className="text-sm">{formik.errors.name}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="age" className="block text-sm font-medium">
                                        Idade
                                    </label>
                                    <input
                                        id="age"
                                        name="age"
                                        type="number"
                                        placeholder="Digite a idade do animal"
                                        {...formik.getFieldProps('age')}
                                        className="w-42 px-3 py-2 border border-gray-300 rounded-xl  focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.age && formik.errors.age ? (
                                        <div className="text-sm">{formik.errors.age}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="type" className="block text-sm font-medium">
                                        Tipo
                                    </label>
                                    <input
                                        id="type"
                                        name="type"
                                        type="text"
                                        placeholder="Digite o tipo do animal"
                                        {...formik.getFieldProps('type')}
                                        className="w-42 px-3 py-2 border border-gray-300 rounded-xl  focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.type && formik.errors.type ? (
                                        <div className="text-sm">{formik.errors.type}</div>
                                    ) : null}
                                </div>
                            </div>
                            <div className="grid grid-flow-col mb-6">
                                <div className="">
                                    <label htmlFor="breed" className="block text-sm font-medium">
                                        Raça
                                    </label>
                                    <input
                                        id="breed"
                                        name="breed"
                                        type="text"
                                        placeholder="Digite a raça do animal"
                                        {...formik.getFieldProps('breed')}
                                        className="w-42 px-3 py-2 border border-gray-300 rounded-xl  focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.breed && formik.errors.breed ? (
                                        <div className="text-sm">{formik.errors.breed}</div>
                                    ) : null}
                                </div>
                                <div className="">
                                    <label htmlFor="ownerId" className="block text-sm font-medium">
                                        ID do dono
                                    </label>
                                    <input
                                        id="ownerId"
                                        name="ownerId"
                                        type="number"
                                        placeholder="Digite o ID do dono do animal"
                                        {...formik.getFieldProps('ownerId')}
                                        className="w-42 px-3 py-2 border border-gray-300 rounded-xl  focus:border-#0588D1 focus:border-[2.8px] focus:outline-none"
                                    />
                                    {formik.touched.ownerId && formik.errors.ownerId ? (
                                        <div className="text-sm">{formik.errors.ownerId}</div>
                                    ) : null}
                                </div>
                            </div>
                            <div className="grid grid-flow-col mb-6 gap-5l place-items-end">
                                <button 
                                    type="submit" 
                                    className="w-36 px-3 py-2 bg-#0588D1 mt-8 p-2 text-white rounded-xl hover:bg-#border-#0588D1"
                                >
                                    Cadastrar
                                </button>
                            </div>
                            {message && <p>
                                <svg className="w-6 h-6 text-red-500 dark:red-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 21">
                                 <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 3.464V1.1m0 2.365a5.338 5.338 0 0 1 5.133 5.368v1.8c0 2.386 1.867 2.982 1.867 4.175C17 15.4 17 16 16.462 16H3.538C3 16 3 15.4 3 14.807c0-1.193 1.867-1.789 1.867-4.175v-1.8A5.338 5.338 0 0 1 10 3.464ZM4 3 3 2M2 7H1m15-4 1-1m1 5h1M6.54 16a3.48 3.48 0 0 0 6.92 0H6.54Z"/>
                                </svg>
                                {message}
                            </p>}
                        </form>
                    </div>
                </FormContainer>
            </AppContainer>
        </div>
    );
}

export default CadastroAnimal;
