import NavBar from "../NavBar"
import { getUsers } from '../../services/user';
import {useEffect, useState} from "react";

function Header({ title }) {
    const [user, setUser] = useState(null);

    useEffect(() => {
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        try {
            const users = await getUsers();
            if (users.length > 0) {
                setUser(users[0]);
            }
        } catch (error) {
            console.error('Erro ao obter os usuários:', error);
        }
    };
    return (
        <> 
            <div className="grid grid-cols-[13rem_auto] items-start h-full">
                <NavBar />
            <main className="flex flex-col items-center">

                <div className="bg-gray-200 w-full h-20 flex items-center">
                    <h1 className="text-#0588D1 text-[35px] ml-3 font-medium">{title}</h1>
                </div>
                <div className="flex items-center justify-center text-pink-500 gap-3">
                    {user ? (
                        <>
                            <h3>{user.name}</h3>
                            <span className="flex justify-center w-9 h-9 bg-white rounded-full border border-gray-300 overflow-hidden">
                                <img src={userImage} className="w-6.3 pt-1" alt="Imagem do Usuário" />
                            </span>
                        </>
                    ) : (
                        <p>Carregando...</p>
                    )}
                </div>

            </main>
            </div>
        </>
    )
}

export default Header;
