import React, { useEffect, useState } from 'react';
import { Suspense, lazy } from 'react';
import { getUsers, patchUsers, deleteUser } from '../../services/user';
import Cookies from 'js-cookie';

//Importação lazy para UsersTable e UserModal
const UsersTable = lazy(() => import('../../components/Editar/index').then(module => ({ default: module.UsersTable })));
const UserModal = lazy(() => import('../../components/Editar/index').then(module => ({ default: module.UserModal })));


function Editar() {
    const [users, setUsers] = useState([]);
    const [editingUser, setEditingUser] = useState(null);
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        birthday: '',
        isAdmin: '',
        id_address: '',
        cep: '',
        street: '',
        city: '',
        state: '',
    });
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [isUnauthorized, setIsUnauthorized] = useState(false);

    useEffect(() => {
        const checkAuthentication = async () => {
            const token = Cookies.get('token');
            if (!token) {
                setIsUnauthorized(true);
                return;
            }

            try {
                const dataUser = await getUsers();
                if (Array.isArray(dataUser)) {
                    setUsers(dataUser);
                } else {
                    setUsers([]);
                }
            } catch (error) {
                console.error('Ops! Ocorreu um erro:', error);
            }
        };

        checkAuthentication();
    }, []);

    const handleEditUser = async (user) => {
        setEditingUser(user);
        setFormData({
            name: user.name,
            email: user.email,
            phone: user.phone,
            birthday: user.birthday,
            isAdmin: user.isAdmin,
            id_address: user.id_address,
            cep: user.address ? user.address.cep : '',
            street: user.address ? user.address.street : '',
            city: user.address ? user.address.city : '',
            state: user.address ? user.address.state : '',
        });
        setIsModalOpen(true);
    };

    const handleFormChange = (event) => {
        setFormData((prevFormData) => ({
            ...prevFormData,
            [event.target.name]: event.target.value,
        }));
    };

    const handleCancelEdit = () => {
        setEditingUser(null);
        setFormData({
            name: '',
            email: '',
            phone: '',
            birthday: '',
            isAdmin: '',
            id_address: '',
            cep: '',
            street: '',
            city: '',
            state: '',
        });
        setIsModalOpen(false);
    };

    const handleSaveEdit = async (values) => {
        try {
          const token = Cookies.get('token');

          if (!token) {
            setIsUnauthorized(true);
            return;
          }

          await patchUsers(editingUser.id, values); // valores do Formik diretamente

          const updatedUsers = await getUsers();
          setUsers(Array.isArray(updatedUsers) ? updatedUsers : []);
          setEditingUser(null);
          setFormData({}); // Reinicie o formData para limpar o formulário
          setIsModalOpen(false);
        } catch (error) {
          console.error('Erro ao atualizar o usuário:', error);
        }
      };


    const handleDeleteUser = async (user) => {
        try {
            await deleteUser(user.id);
            const updatedUsers = await getUsers();
            if (Array.isArray(updatedUsers)) {
                setUsers(updatedUsers);
            } else {
                setUsers([]);
            }
        } catch (error) {
            console.error('Erro ao excluir o usuário:', error);
        }
    };

    return (
        <div>
            {isUnauthorized ? (
                <div className="flex items-center bg-red-100 p-4 rounded-md mb-4">
                    <p className="text-red-500">Usuário não autorizado</p>
                </div>
            ) : (

                <Suspense fallback={<p>Carregando...</p>}>
                    <div className='h-full'>

                            <UsersTable users={users} onEditUser={handleEditUser} onDeleteUser={handleDeleteUser} />

                            <UserModal
                                isOpen={isModalOpen}
                                formData={formData}
                                onChange={handleFormChange}
                                onCancel={handleCancelEdit}
                                onSave={handleSaveEdit}
                            />
                    </div>
                </Suspense>
            )}


        </div>
    );
}

export default Editar;
