import React from "react";

function DeletePetModal({ isOpen, onClose, onDelete, pet }) {
    const handleDelete = () => {
        // Faça a lógica para excluir o pet no servidor aqui
        // Depois de excluir com sucesso, chame onDelete() e feche o modal
        onDelete(pet.id);
        onClose();
    };

    return (
        <div className={`modal ${isOpen ? "open" : ""}`}>
            <div className="modal-content">
                <h2>Excluir Pet</h2>
                <p>Tem certeza que deseja excluir o pet "{pet.name}"?</p>
                <button onClick={handleDelete}>Sim</button>
                <button onClick={onClose}>Não</button>
            </div>
        </div>
    );
}

export default DeletePetModal;
