import React, { useState } from 'react';
import Cookies from 'js-cookie';
import { useFormik } from 'formik';
import * as Yup from 'yup';
import { IMaskInput } from "react-imask";

import LogoLogin from "../../img/LOGO LOGIN v2.png"
import ShowPasswordImg from "../../img/ShowPasswordImg 1.png";
import HidePasswordImg from "../../img/HidePasswordImg 1.png";

const Login = () => {
    const [cpf, setcpf] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [showPassword, setShowPassword] = useState(false);

    const formik = useFormik({
        initialValues: {
            cpf: '',
            password: '',
        },
        validationSchema: Yup.object({
            cpf: Yup.string()
                .required('Obrigatório'),
            password: Yup.string()
                .min(6, "Minimo de 6 caracteres")
                .required('Obrigatório'),
        }),

        onSubmit: async (values) => {

            try {
                const response = await fetch('http://54.90.79.152/user/login', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify(values),
                });

                if (response.ok) {
                    const { token } = await response.json();
                    Cookies.set('token', token, { /*secure: true,*/ sameSite: 'strict' });
                    window.location.href = '/';
                } else {
                    setError('Usuário ou senha incorretos');
                }
            } catch (error) {
                console.log('Erro na solicitação de autenticação', error);
                setError('Ocorreu um erro na solicitação de autenticação');
            }
        },
    });

    //"Ocultar senha" : "Mostrar senha"
    const togglePasswordVisibility = () => {
        setShowPassword((prevShowPassword) => !prevShowPassword);
    };

    return (
        <div className='bg-#243D96 w-screen h-screen m-0'>
            <div>
                <form onSubmit={formik.handleSubmit} className='flex flex-col m-0'>
                    <div class="w-64">
                        <img
                            src={LogoLogin}
                            alt="imagem de login"
                            className="w-full h-auto 
                                        xl:w-715 xl:h-498 xl:max-w-none /*responsivo 'xl:'*/
                                        xxl:w-900 xxl:h-600 "           //responsivo 'xxl:'
                        />
                    </div>

                    <div className="">
                        <label className="flex p-1 pl-3 flex-col justify-center flex-shrink-0 text-xl font-normal  text-gray-200  
                                            xl:text-5xl xl:p-8"//responsivo 'xl:'
                        >CPF:</label>
                        <IMaskInput
                            className=" flex px-8 py-2 w-753 h-115 rounded-xl bg-gray-200 text-center font-medium  
                                        xl:px-24 xl:text-5xl xl:rounded-3xl /*responsivo 'xl:'*/
                                        xxl:text-6xl" //*responsivo 'xxl:'
                            id="cpf"
                            name="cpf"
                            type="text"
                            mask="000.000.000-00"
                            placeholder="Ex. 000.000.000-00"
                            {...formik.getFieldProps('cpf')}
                        />
                        {formik.touched.cpf && formik.errors.cpf ? (
                            <div>{formik.errors.cpf}</div>
                        ) : null}

                        <div className='relative'>
                            <label className="flex p-1 pl-3 flex-col justify-center flex-shrink-0 text-xl font-normal  text-gray-200  
                                        xl:text-5xl xl:p-8 pr-12"//responsivo 'xl:'
                            >Senha:</label>
                            <div className='relative'>
                                <input
                                    className="flex px-8 py-2 w-753 h-115 rounded-xl bg-gray-200 text-center font-medium
                                            xl:px-24 xl:text-5xl xl:rounded-3xl /*responsivo 'xl:'*/
                                            xxl:text-6xl" //*responsivo 'xxl:'"
                                    type={showPassword ? 'text' : 'password'}
                                    placeholder=""
                                    {...formik.getFieldProps('password')}
                                />
                                <img
                                    src={showPassword ? HidePasswordImg : ShowPasswordImg}
                                    alt={showPassword ? "Ocultar senha" : "Mostrar senha"}
                                    className="absolute right-2 top-1/3 transform mr-2 mt-2 -translate-y-1/2 cursor-pointer h-6"
                                    onClick={togglePasswordVisibility}
                                />
                            </div>
                            {formik.touched.password && formik.errors.password ? (
                                <div>{formik.errors.password}</div>
                            ) : null}

                        </div>
                        <div className='flex justify-center mt-8 xl:mt-28'>
                            <button
                                className=" m-0 flex-shrink-0 bg-green-500 hover:bg-green-400 hover:text-black text-white py-2 px-10 rounded-xl 
                                        xl:px-20 xl:py-6 xl:text-5xl xl:rounded-3xl"//responsivo 'xl:'
                                type="submit"
                            >
                                Entrar
                            </button>
                        </div>

                        <a href='#' className='flex justify-center text-gray-200 mt-3 hover:text-white 
                                                xl:text-5xl xl:mt-14 '//responsivo 'xl:'
                        >Esqueci minha senha</a>

                        {error && <p className="text-red-500 mt-2 justify-center flex">
                            <svg className="w-6 h-6 text-red-500 dark:red-500" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 20 21">
                                <path stroke="currentColor" strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 3.464V1.1m0 2.365a5.338 5.338 0 0 1 5.133 5.368v1.8c0 2.386 1.867 2.982 1.867 4.175C17 15.4 17 16 16.462 16H3.538C3 16 3 15.4 3 14.807c0-1.193 1.867-1.789 1.867-4.175v-1.8A5.338 5.338 0 0 1 10 3.464ZM4 3 3 2M2 7H1m15-4 1-1m1 5h1M6.54 16a3.48 3.48 0 0 0 6.92 0H6.54Z" />
                            </svg>
                            {error}</p>}

                    </div>
                </form>
            </div>
            <div class="custom-shape-divider-bottom-1694098061 relative">
                <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1200 120" preserveAspectRatio="none"
                    class="block w-[100%] h-177px  transform rotate-180">
                    <path
                        class="shape-fill fill-current text-blue-500 opacity-25"
                        d="M0,0V46.29c47.79,22.2,103.59,32.17,158,28,70.36-5.37,136.33-33.31,206.8-37.5C438.64,32.43,512.34,53.67,583,72.05c69.27,18,138.3,24.88,209.4,13.08,36.15-6,69.85-17.84,104.45-29.34C989.49,25,1113-14.29,1200,52.47V0Z"
                    ></path>
                    <path
                        class="shape-fill fill-current text-blue-500 opacity-50"
                        d="M0,0V15.81C13,36.92,27.64,56.86,47.69,72.05,99.41,111.27,165,111,224.58,91.58c31.15-10.15,60.09-26.07,89.67-39.8,40.92-19,84.73-46,130.83-49.67,36.26-2.85,70.9,9.42,98.6,31.56,31.77,25.39,62.32,62,103.63,73,40.44,10.79,81.35-6.69,119.13-24.28s75.16-39,116.92-43.05c59.73-5.85,113.28,22.88,168.9,38.84,30.2,8.66,59,6.17,87.09-7.5,22.43-10.89,48-26.93,60.65-49.24V0Z"
                    ></path>
                    <path class="shape-fill fill-current text-blue-500" d="M0,0V5.63C149.93,59,314.09,71.32,475.83,42.57c43-7.64,84.23-20.12,127.61-26.46,59-8.63,112.48,12.24,165.56,35.4C827.93,77.22,886,95.24,951.2,90c86.53-7,172.46-45.71,248.8-84.81V0Z"></path>
                </svg>
            </div>
        </div>
    );
};

export default Login;
