import React, { useState, useEffect } from 'react';
import SeachAnimal from '../SeachAnimal/index.';
import pata from '../../img/patas.png';
import { getPet, getPets, patchPet, deletePet } from '../../services/pets'; // Importe as funções patchPet e deletePet
import { getUser } from '../../services/user';
import EditPetModal from '../EditPetModal';
import PetActionsModal from '../PetActionsModal'; // Importe o novo componente

function EditarAnimal() {
    const [pets, setPets] = useState([]);
    const [selectedPetId, setSelectedPetId] = useState(null);
    const [isEditModalOpen, setEditModalOpen] = useState(false);
    const [selectedPetData, setSelectedPetData] = useState(null);
    const [dataUpdated, setDataUpdated] = useState(false); // Adicione o estado para controlar as atualizações
    const [petToDelete, setPetToDelete] = useState(null); // Adicione o estado para rastrear o pet a ser excluído
    const [isPetActionsModalOpen, setPetActionsModalOpen] = useState(false); // Adicione o estado para controlar a modal de ações do pet

    useEffect(() => {
        async function fetchPets() {
            try {
                const petList = await getPets();
                const petsWithOwnerNames = await Promise.all(
                    petList.map(async (pet) => {
                        try {
                            const user = await getUser(pet.ownerId);
                            console.log(`Nome do responsável para o pet com ID ${pet.id}: ${user.dataUser.name}`);
                            return { ...pet, ownerName: user.dataUser.name };
                        } catch (error) {
                            console.error(`Erro ao obter o usuário para o pet com ID ${pet.id}:`, error);
                            return { ...pet, ownerName: 'Não encontrado' };
                        }
                    })
                );
                setPets(petsWithOwnerNames);
            } catch (error) {
                console.error('Erro ao obter os pets:', error);
            }
        }

        fetchPets();
    }, [dataUpdated]);

    const handleEditClick = async (petId) => {
        setSelectedPetId(petId);
        try {
            const pet = await getPet(petId);
            setSelectedPetData(pet);
            setEditModalOpen(true);
        } catch (error) {
            console.error('Erro ao obter os dados do pet:', error);
        }
    };

    const handleDeleteClick = (petId) => {
        // Rastreie o pet a ser excluído
        const petToDelete = pets.find((pet) => pet.id === petId);
        setPetToDelete(petToDelete);
        setPetActionsModalOpen(true); // Abra o modal de ações do pet
    };

    const handleDeleteConfirmed = async () => {
        if (petToDelete) {
            try {
                await deletePet(petToDelete.id);
                setDataUpdated(!dataUpdated); // Atualize a lista de pets
                setPetActionsModalOpen(false); // Feche o modal de ações do pet
                setPetToDelete(null); // Limpe o pet a ser excluído
            } catch (error) {
                console.error('Erro ao excluir o pet:', error);
            }
        }
    };

    return (
        <div className='relative ml-[195px] -mt-[650px]'>
            <section className="flex justify-between mx-8 mt-4 items-center">
                <SeachAnimal />
                <div className="flex items-center">
                    <a
                        href="/cadastroAnimal"
                        className="w-50 px-3 py-2 bg-#0588D1 p-2 text-white rounded-xl hover:bg-blue-800 flex"
                    >
                        <svg
                            xmlns="http://www.w3.org/2000/svg"
                            fill="none"
                            viewBox="0 0 24 24"
                            strokeWidth={1.5}
                            stroke="currentColor"
                            className="w-6 h-6"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                d="M12 4.5v15m7.5-7.5h-15"
                            />
                        </svg>
                        Cadastrar
                    </a>
                </div>
            </section>

            <table className="mx-8 mt-4 bg-gray-200 rounded-xl mb-10 w-[1100px]">
                {/* ... Tabela de pets */}
                {pets.map((pet) => (
                    <tr key={pet.id} className="space-x-8 border-t-2 border-white text-gray-700 text-center">
                        {/* ... Colunas da tabela */}
                        <td className="px-8 ">
                            <button onClick={() => handleEditClick(pet.id)} className="hover:bg-gray-400 rounded">
                                {/* Botão de editar */}
                                <svg
                                    xmlns="http://www.w3.org/2000/svg"
                                    fill="none"
                                    viewBox="0 0 24 24"
                                    strokeWidth={1.5}
                                    stroke="currentColor"
                                    className="w-6 h-6"
                                >
                                    <path
                                        strokeLinecap="round"
                                        strokeLinejoin="round"
                                        d="M6.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0zM12.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0zM18.75 12a.75.75 0 11-1.5 0 .75.75 0 011.5 0z"
                                    />
                                </svg>
                            </button>
                            <button onClick={() => handleDeleteClick(pet.id)} className="hover:bg-gray-400 rounded">
                                {/* Botão de excluir */}
                                <svg
                                    xmlns="http://www.w3.org/2000/svg"
                                    fill="none"
                                    viewBox="0 0 24 24"
                                    strokeWidth={1.5}
                                    stroke="currentColor"
                                    className="w-6 h-6"
                                >
                                    <path
                                        strokeLinecap="round"
                                        strokeLinejoin="round"
                                        d="M6.37 3.293a.75.75 0 011.06 0l5.293 5.293 5.293-5.293a.75.75 0 111.06 1.06L13.06 9.06a.75.75 0 01-1.06 0L6.37 4.354a.75.75 0 010-1.06z"
                                    />
                                    <path
                                        strokeLinecap="round"
                                        strokeLinejoin="round"
                                        d="M15 19a2 2 0 01-2-2V8a2 2 0 012-2h4a2 2 0 012 2v9a2 2 0 01-2 2h-4z"
                                    />
                                </svg>
                            </button>
                        </td>
                    </tr>
                ))}
            </table>

            {isEditModalOpen && (
                <EditPetModal
                    petId={selectedPetId}
                    petData={selectedPetData}
                    onClose={() => setEditModalOpen(false)}
                    onUpdatePetData={() => setDataUpdated(!dataUpdated)} // Atualize dataUpdated ao salvar
                />
            )}

            {/* Modal de ações do pet */}
            {isPetActionsModalOpen && (
                <PetActionsModal
                    isOpen={isPetActionsModalOpen}
                    onClose={() => setPetActionsModalOpen(false)}
                    onEdit={() => handleEditClick(petToDelete.id)}
                    onDelete={handleDeleteConfirmed} // Exclua o pet ao confirmar a exclusão
                />
            )}
        </div>
    );
}

export default EditarAnimal;
