import React, { useEffect, useState } from 'react';
import userImage from '../../img/user2.png';
import NavBar from '../../components/NavBar/index';
import { getUsers } from '../../services/user';

function Header(props) {
    const [user, setUser] = useState(null);

    useEffect(() => {
        fetchUserData();
    }, []);

    const fetchUserData = async () => {
        try {
            const users = await getUsers();
            if (users.length > 0) {
                setUser(users[0]);
            }
        } catch (error) {
            console.error('Erro ao obter os usuários:', error);
        }
    };

    // <div className="sc-gTRrQi dEbGMU bg-gray-200 w-full">
    // <h1 className="flex items-center justify-center text-pink-500">

    return (
        <div className="grid grid-cols-[13rem_auto] items-start flex">
            <NavBar />
            <header className="bg-gray-200 flex justify-between h-20 p-0 3rem " >
                <h1 class="ml-4 flex items-center justify-center text-pink-500 text-[40px] font-bold">{props.title}</h1>
                <div className="flex items-center justify-center text-pink-500 gap-3">
                    {user ? (
                        <>
                            <h3>{user.name}</h3>
                            <span className="flex justify-center w-9 h-9 bg-white rounded-full border border-gray-300 overflow-hidden">
                                <img src={userImage} className="w-6.3 pt-1" alt="Imagem do Usuário" />
                            </span>
                        </>
                    ) : (
                        <p>Carregando...</p>
                    )}
                </div>
            </header>

        </div>
    );
}

export default Header;
