import React, { useState, useEffect } from 'react';
import Modal from '../Modal'; // Importe um componente de modal ou use sua própria implementação

function EditPetModal({ petId, onClose }) {
    const [petData, setPetData] = useState({
        name: '',
        age: '',
        breed: '',
        // Adicione outros campos do pet aqui
    });

    // Carregue os dados do pet com base no ID quando o componente for montado
    useEffect(() => {
        async function fetchPetData() {
            try {
                // Faça uma chamada à API para obter os dados do pet com o ID especificado
                // Substitua esta chamada pela sua própria lógica de obtenção de dados
                const response = await fetch(`/api/pets/${petId}`);
                const data = await response.json();
                setPetData(data); // Atualize o estado com os dados do pet
            } catch (error) {
                console.error('Erro ao carregar os dados do pet:', error);
            }
        }

        fetchPetData();
    }, [petId]);

    // Lida com a atualização dos campos do pet
    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPetData({
            ...petData,
            [name]: value,
        });
    };

    // Lida com o envio do formulário de edição do pet
    const handleSave = async () => {
        try {
            // Faça uma chamada à API para atualizar os dados do pet
            // Substitua esta chamada pela sua própria lógica de atualização de dados
            await fetch(`/api/pets/${petId}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(petData),
            });
            onClose(); // Feche o modal após a edição ser concluída com sucesso
        } catch (error) {
            console.error('Erro ao salvar as alterações no pet:', error);
        }
    };

    return (
        <Modal onClose={onClose}>
            <h2>Editar Pet</h2>
            <form>
                <div>
                    <label htmlFor="name">Nome:</label>
                    <input
                        type="text"
                        id="name"
                        name="name"
                        value={petData.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div>
                    <label htmlFor="age">Idade:</label>
                    <input
                        type="text"
                        id="age"
                        name="age"
                        value={petData.age}
                        onChange={handleInputChange}
                    />
                </div>
                <div>
                    <label htmlFor="breed">Raça:</label>
                    <input
                        type="text"
                        id="breed"
                        name="breed"
                        value={petData.breed}
                        onChange={handleInputChange}
                    />
                </div>
                {/* Adicione outros campos de edição do pet aqui */}
                <button type="button" onClick={handleSave}>Salvar</button>
            </form>
        </Modal>
    );
}

export default EditPetModal;
