import React, { useState, useEffect } from 'react';
import { getPet, patchPet } from '../../services/pets';

function EditPetModal({ petId, petData, onClose }) {
    const [petDataModal, setPetDataModal] = useState(petData); // Inicialize o estado com os dados do pet

    useEffect(() => {
        async function fetchPetData() {
            try {
                const pet = await getPet(petId);
                setPetDataModal(pet); // Atualize o estado com os dados do pet
            } catch (error) {
                console.error('Erro ao carregar os dados do pet:', error);
            }
        }

        fetchPetData();
    }, [petId]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setPetDataModal({
            ...petDataModal,
            [name]: value,
        });
    };

    const handleSave = async () => {
        try {
            const updatedPet = await patchPet(petId, petDataModal);
            setPetDataModal(updatedPet);
            onClose();
        } catch (error) {
            console.error('Erro ao salvar as alterações no pet:', error);
        }
    };

    return (
        <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-gray-800 bg-opacity-70 z-50">
            <div className="bg-white p-4 rounded-lg shadow-lg">
                <h2 className="text-lg font-semibold mb-4">Editar Pet</h2>
                {petDataModal && (
                    <form>
                        <div className="mb-4">
                            <label htmlFor="name" className="block text-sm font-medium">
                                Nome do Pet
                            </label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={petDataModal.name}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm"
                            />
                        </div>
                        <div className="mb-4">
                            <label htmlFor="age" className="block text-sm font-medium">
                                Idade do Pet
                            </label>
                            <input
                                type="text"
                                id="age"
                                name="age"
                                value={petDataModal.age}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm"
                            />
                        </div>
                        <div className="mb-4">
                            <label htmlFor="breed" className="block text-sm font-medium">
                                Raça do Pet
                            </label>
                            <input
                                type="text"
                                id="breed"
                                name="breed"
                                value={petDataModal.breed}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm"
                            />
                        </div>
                        <div className="flex justify-end">
                            <button
                                type="button"
                                onClick={handleSave}
                                className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300"
                            >
                                Salvar
                            </button>
                            <button
                                type="button"
                                onClick={onClose}
                                className="ml-2 bg-gray-300 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-400 focus:outline-none focus:ring focus:ring-gray-300"
                            >
                                Cancelar
                            </button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
}

export default EditPetModal;
