import React, {useState} from "react";

function EditPetModal({isOpen, onClose, pet, onUpdate}) {
    const [editedPet, setEditedPet] = useState({...pet});

    const handleInputChange = (e) => {
        const {name, value} = e.target;
        setEditedPet({...editedPet, [name]: value});
    };

    const handleSubmit = () => {
        // Faça a lógica para atualizar o pet no servidor aqui
        // Depois de atualizar com sucesso, chame onUpdate() e feche o modal
        onUpdate(editedPet);
        onClose();
    };

    return (
        <div className={`modal ${isOpen ? "open" : ""}`}>
            <div className="modal-content">
                <h2>Editar Pet</h2>
                <label htmlFor="name">Nome:</label>
                <input
                    type="text"
                    name="name"
                    value={editedPet.name}
                    onChange={handleInputChange}
                />
                {/* Adicione campos para outros detalhes do pet aqui */}
                <button onClick={handleSubmit}>Salvar</button>
                <button onClick={onClose}>Cancelar</button>
            </div>
        </div>
    );
}

export default EditPetModal;
