import React, { useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Cookies from 'js-cookie';
import Cadastro from './pages/cadastro/';
import Home from './pages/home/';
import Editar from './pages/editar/';
import Header from './components/Header';
import LoginRoute from './components/LoginRoute';
import PrivateRoute from './components/PrivateRoute';

const routeTitles = {
    '/': 'Página Inicial',
    '/cadastro': 'Cadastro',
    '/editar': 'Editar',
    '/estoque': 'Cadastro de Estoque',
    '/cadastro_material': 'Cadastro de Material',
};

const App = () => {
    const location = window.location.pathname;
    const isLoginPage = location === '/login';

    const pageTitle = routeTitles[location] || 'Página Desconhecida';
    

    useEffect(() => {
        const token = Cookies.get('token');

        if (!token && !isLoginPage) {
            window.location.href = '/login';
        }
    }, []);

    return (
        <div>
            {!isLoginPage && <Header title={pageTitle} cl />}
            <Routes>
                <Route path="/" element={<PrivateRoute component={<Home />} />} />
                <Route path="/cadastro" element={<PrivateRoute component={<Cadastro />} />} />
                <Route path="/editar" element={<PrivateRoute component={<Editar />} />} />
                <Route path="/login" element={<LoginRoute />}/>
            </Routes>
        </div>
    );
};

const Router = () => {
    return (
        <BrowserRouter>
            <App />
        </BrowserRouter>
    );
};

export default Router;
