import React, { useState, useEffect } from 'react';
import { getPet, patchPet, postPet } from '../../services/pets';

function EditPetModal({ petId, onClose }) {
    const [petData, setPetData] = useState(null);
    const [editedPetData, setEditedPetData] = useState({
        pet: {
            name: '',
            age: '',
            breed: '',
        },
    });

    useEffect(() => {
        async function fetchPetData() {
            try {
                const pet = await getPet(petId);
                console.log('Dados do pet:', pet.name); // Verifique se os dados do pet são exibidos no console
                setPetData(pet);
                setEditedPetData({
                    pet: { ...pet },
                });
            } catch (error) {
                console.error('Erro ao carregar os dados do pet:', error);
            }
        }

        fetchPetData();
    }, [petId]);

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setEditedPetData((prevState) => ({
            pet: {
                ...prevState.pet,
                [name]: value,
            },
        }));
    };

    const handleSave = async () => {
        try {
            if (petId) {
                // Se petId existe, então é uma edição
                const updatedPet = await patchPet(petId, editedPetData.pet);
                setPetData(updatedPet);
            } else {
                // Caso contrário, é um novo cadastro
                const newPet = await postPet(editedPetData.pet);
                // Lógica para tratar o novo pet, se necessário
            }
            onClose();
        } catch (error) {
            console.error('Erro ao salvar as alterações no pet:', error);
        }
    };

    return (
        <div className="fixed top-0 left-0 w-full h-full flex justify-center items-center bg-gray-800 bg-opacity-70 z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h2 className="text-2xl font-semibold mb-4">Editar Pet</h2>
                {petData && (
                    <form>
                        <div className="mb-4">
                            <label htmlFor="name" className="block text-sm font-medium">
                                Nome do Pet
                            </label>
                            <input
                                type="text"
                                id="name"
                                name="name"
                                value={editedPetData.pet.pet.name}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm p-2"
                            />
                        </div>
                        <div className="mb-4">
                            <label htmlFor="age" className="block text-sm font-medium">
                                Idade do Pet
                            </label>
                            <input
                                type="text"
                                id="age"
                                name="age"
                                value={editedPetData.pet.pet.age}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm p-2"
                            />
                        </div>
                        <div className="mb-4">
                            <label htmlFor="breed" className="block text-sm font-medium">
                                Raça do Pet
                            </label>
                            <input
                                type="text"
                                id="breed"
                                name="breed"
                                value={editedPetData.pet.pet.breed}
                                onChange={handleInputChange}
                                className="w-full border-gray-300 rounded-md shadow-sm p-2"
                            />
                        </div>
                        <div className="flex justify-end">
                            <button
                                type="button"
                                onClick={handleSave}
                                className="bg-blue-500 text-white py-2 px-4 rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-300"
                            >
                                Salvar
                            </button>
                            <button
                                type="button"
                                onClick={onClose}
                                className="ml-2 bg-gray-300 text-gray-700 py-2 px-4 rounded-md hover:bg-gray-400 focus:outline-none focus:ring focus:ring-gray-300"
                            >
                                Cancelar
                            </button>
                        </div>
                    </form>
                )}
            </div>
        </div>
    );
}

export default EditPetModal;
